mapper.update:
update student_course
set grade = #{grade}
where sid = #{sid} and cid = #{cid}

mapper.select:
select sum(course.ccredit) from student_course
join course on student_course.cid = course.cid


        student_course.sid=#{sid}

select sum(course.ccredit) from student_course join course on student_course.cid = course.cid
student_course.grade>=60
and student_course.sid=#{sid}

select student_course.*,student.sname,student.ssex,student.sage,course.cname,course.ccredit,teacher.tname, case when exists (select 1 from student_course sc where sc.sid = student.sid and sc.cid = course.cid) then 1 else 0 end as status from student cross join course left join student_course on student.sid = student_course.sid and course.cid = student_course.cid join teacher on course.tid = teacher.tid
student.sid=#{sid}
and course.cid=#{studentCourse.cid}
